Scripts.OnClick:
if (getglobal(self:GetText().."_raid_name"):GetText() ~= nil) then
	PlaySound("igMainMenuOptionCheckBoxOn");
	RaidPlanerCalendar:Hide();
	GetDKPRaidPlanerRaidFrame:Show();
	
	prefix = self:GetText();
	timestamp = tostring(getglobal(prefix.."_raid_name").timestamp);
	GDR_Scroll_DB(timestamp);
end;
